ValidationRule.errorConditionFormula:
AND(
    ISCHANGED(OwnerId),
        OR(
												AND(
																ISNULL(Country),
																NOT($Profile.Name == 'System Administrator')
												),
												ISPICKVAL(LeadSource, ''),
												AND(
																ISNULL(NumberOfEmployees),
																NOT(
																				CONTAINS(
																								'System Administrator,Contract Manager',
																								$Profile.Name
																				)
																)
												)
								)
)
